import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Educacion } from 'src/app/model/educacion';
import { EducacionService } from 'src/app/service/educacion.service';
import { ImageEduService } from 'src/app/service/image-edu.service';

@Component({
  selector: 'app-edit-educacion',
  templateUrl: './edit-educacion.component.html',
  styleUrls: ['./edit-educacion.component.css']
})
export class EditEducacionComponent implements OnInit{



  edu: Educacion = null;

  constructor(private educacionService: EducacionService, private activatedRouter: ActivatedRoute, private router: Router, public imageEduService: ImageEduService) {}
  
  ngOnInit(): void {
    const id = this.activatedRouter.snapshot.params['idEdu'];
    this.educacionService.detail(id).subscribe(data => {
      this.edu= data;
    }, err => {
      alert("Error al modificar educación");
      this.router.navigate(['']);
    })
  }

  onUpdate(): void {
    const id = this.activatedRouter.snapshot.params['idEdu'];
    
    this.educacionService.updateEducacion(id, this.edu).subscribe(data => {
      this.router.navigate(['']);
    }, err => {
      alert("Error al modificar educación");
      this.router.navigate(['']);
    })
  }

}
