import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Experiencia } from 'src/app/model/experiencia';
import { ExperienciaService } from 'src/app/service/experiencia.service';

@Component({
  selector: 'app-new-experiencia',
  templateUrl: './new-experiencia.component.html',
  styleUrls: ['./new-experiencia.component.css']
})
export class NewExperienciaComponent implements OnInit {
  tituloExp: string = '';
  fechaExp: string = '';
  descExp: string = '';
  imgExp: string = '';

  constructor(private experienciaService: ExperienciaService, private router: Router){}



  ngOnInit(): void {

  }

  onCreate(): void {
    const expe = new Experiencia (this.tituloExp, this.fechaExp, this.descExp, this.imgExp);
    this.experienciaService.addExperiencia(expe).subscribe({
      next:(data) => {
      alert("Experiencia añadida");
      this.router.navigate(['']);
    },
     error:(err) => {
      alert("Falló");
      this.router.navigate(['']);
    }

  });

}}
<div class="container">
    <div class="row">
        <div>
            <form (ngSubmit)="onCreate()" novalidate #f="ngForm">
                <div class="form group">
                    <label for="tituloExp">Puesto Laboral:</label>
                    <input type="text" class="form-control" id="tituloExp" name="tituloExp" [(ngModel)]="tituloExp"
                        required>
                </div>
                <div class="form group">
                    <label for="fechaExp">Período de trabajo:</label>
                    <input type="text" class="form-control" id="fechaExp" name="fechaExp" [(ngModel)]="fechaExp"
                        required>
                </div>
                <div class="form group">
                    <label for="descExp">Descripción del puesto:</label>
                    <input type="text" class="form-control" id="descExp" name="descExp" [(ngModel)]="descExp" required>
                </div>
                <button class="btn btn-outline-light" type="submit">
                    Agregar Experiencia
                </button>
            </form>
        </div>
    </div>
</div>

