import { Injectable } from '@angular/core';
import { Storage, getDownloadURL, list, ref, uploadBytes} from '@angular/fire/storage';
import { async } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class ImageEduService {
  url: string = '';

  constructor(private storage: Storage) {}

  public uploadImg($event: any, name: string){
    const file = $event.target.files[0]
    const imgRef = ref(this.storage, `educacion/`+ name)
    uploadBytes(imgRef, file)
    .then(response =>{
      this.getImages(name)
    })
    .catch(error => console.log(error)
    )
  }
  getImages(name: string){
    const imagesRef = ref(this.storage, `educacion/`+ name)
    list(imagesRef)
    .then(async response => {
      for (let item of response.items){
        this.url = await getDownloadURL(item);
        console.log(this.url);

      }
    })
    .catch(error => console.log(error))
  } 
  }
