import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Educacion } from 'src/app/model/educacion';
import { EducacionService } from 'src/app/service/educacion.service';


@Component({
  selector: 'app-new-educacion',
  templateUrl: './new-educacion.component.html',
  styleUrls: ['./new-educacion.component.css']
})
export class NewEducacionComponent implements OnInit {
  tituloEdu: string = '';
  fechaEdu: string = '';
  descEdu: string = '';
  imgEdu: string = '';

  constructor(private educacionService: EducacionService, private router: Router){}

  
  
  ngOnInit(): void {

  }

  onCreate(): void {
    const edu = new Educacion (this.tituloEdu, this.fechaEdu, this.descEdu, this.imgEdu);
    this.educacionService.addEducacion(edu).subscribe({
      next:(data) => {
      alert("Educación añadida");
      this.router.navigate(['']);
    },
     error:(err) => {
      alert("Falló");
      this.router.navigate(['']);
    }
    
  });
  
}


}


<div class="container">
    <div class="row">
        <div>
            <form (ngSubmit)="onCreate()" novalidate #f="ngForm">
                <div class="form group">
                    <label for="tituloEdu">Título:</label>
                    <input type="text" class="form-control" id="tituloEdu" name="tituloEdu" [(ngModel)]="tituloEdu"
                        required>
                </div>
                <div class="form group">
                    <label for="fechaExp">Año de egreso:</label>
                    <input type="text" class="form-control" id="fechaEdu" name="fechaEdu" [(ngModel)]="fechaEdu"
                        required>
                </div>
                <div class="form group">
                    <label for="descExp">Descripción de la formación:</label>
                    <input type="text" class="form-control" id="descEdu" name="descEdu" [(ngModel)]="descEdu" required>
                </div>
                <button class="btn btn-outline-light" type="submit">
                    Agregar Educación
                </button>
            </form>
        </div>
    </div>
</div>
