import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Experiencia } from 'src/app/model/experiencia';
import { ExperienciaService } from 'src/app/service/experiencia.service';

@Component({
  selector: 'app-edit-experiencia',
  templateUrl: './edit-experiencia.component.html',
  styleUrls: ['./edit-experiencia.component.css']
})
export class EditExperienciaComponent implements OnInit {

  expLab: Experiencia = null;

  constructor(private experienciaService: ExperienciaService, private activatedRouter: ActivatedRoute, private router: Router) {}

  ngOnInit(): void {
    const id = this.activatedRouter.snapshot.params['id'];
    this.experienciaService.detail(id).subscribe(data => {
      this.expLab= data;
    }, err => {
      alert("Error al modificar experiencia");
      this.router.navigate(['']);
    })
  }

  onUpdate(): void {
    const id = this.activatedRouter.snapshot.params['id'];
    this.experienciaService.updateExperiencia(id, this.expLab).subscribe(data => {
      this.router.navigate(['']);
    }, err => {
      alert("Error al modificar experiencia");
      this.router.navigate(['']);
    })
  }

}
<div class="container">
    <div class="row">
        <div>
            <form (ngSubmit)="onUpdate()" novalidate #f="ngForm">
                <div class="form group">
                    <label for="tituloExp">Puesto Laboral:</label>
                    <input type="text" class="form-control" id="tituloExp" name="tituloExp" [(ngModel)]="tituloExp"
                        required>
                </div>
                <div class="form group">
                    <label for="fechaExp">Período de trabajo:</label>
                    <input type="text" class="form-control" id="fechaExp" name="fechaExp" [(ngModel)]="fechaExp"
                        required>
                </div>
                <div class="form group">
                    <label for="descExp">Descripción del puesto:</label>
                    <input type="text" class="form-control" id="descExp" name="descExp" [(ngModel)]="descExp" required>
                </div>
                <div class="form group">
                    <label for="imgExp">Logo del lugar de trabajo:</label>
                    <input type="text" class="form-control" id="imgExp" name="imgExp" [(ngModel)]="imgExp" required>
                </div>
                <button class="btn btn-outline-light" type="submit">
                    Agregar Experiencia
                </button>
            </form>
        </div>
    </div>
</div>
